const nomField = document.getElementById("nomMission");
const atelierFields = document.getElementById("atelier-fields");
const auditFields = document.getElementById("audit-fields");
const conferenceFields = document.getElementById("conference-fields");
const seminaireFields = document.getElementById("seminaire-fields");
const interventionFields = document.getElementById("intervention-fields");
const controleFields = document.getElementById("controle-fields");
const reunionFields = document.getElementById("reunion-fields");
nomField.addEventListener("change", function () {
    atelierFields.style.display = "none";
    auditFields.style.display = "none";
    seminaireFields.style.display = "none";
    interventionFields.style.display = "none";
    controleFields.style.display = "none";
    conferenceFields.style.display = "none";
    reunionFields.style.display = "none";
    if (this.value === "atélier") {
        atelierFields.style.display = "block";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var theme = document.getElementById("theme");
        var nomDirigeant = document.getElementById("nomDirigeant");
        var nomRapporteur = document.getElementById("nomRapporteur");
        var objectifSpecifique = document.getElementById("objectifSpecifique");
        theme.required = true;
        nomDirigeant.required = true;
        nomRapporteur.required = true;
        objectifSpecifique.required = true;
    } else if (this.value === "audit") {
        auditFields.style.display = "block";
        atelierFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        representationFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var typeAC = document.getElementById("typeAC");
        var structure_id = document.getElementById("structure_id");
        var methodologie = document.getElementById("methodologie");
        var delaisMiseOeuvre = document.getElementById("delaisMiseOeuvre");
        typeAC.required = true;
        structure_id.required = true;
        methodologie.required = true;
        delaisMiseOeuvre.required = true;
    } else if (this.value === "conférence") {
        conferenceFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        representationFields.style.display = "none";
        controleFields.style.display = "none";
        reunionFields.style.display = "none";
        var theme = document.getElementById("themeC");
        var nomConferencier = document.getElementById("nomConferencier");
        var nomRapporteur = document.getElementById("nomRapporteurC");
        var nomAnimateur = document.getElementById("nomAnimateur");
        var description = document.getElementById("description");
        theme.required = true;
        nomConferencier.required = true;
        nomRapporteur.required = true;
        nomAnimateur.required = true;
        description.required = true;
    } else if (this.value === "séminaire") {
        seminaireFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        interventionFields.style.display = "none";
        representationFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var theme = document.getElementById("themeS");
        var nomAnimateur = document.getElementById("nomAnimateurS");
        theme.required = true;
        nomAnimateur.required = true;
    } else if (this.value === "intervention") {
        interventionFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        representationFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var nature = document.getElementById("nature");
        var sujet = document.getElementById("sujet");
        nature.required = true;
        sujet.required = true;
    } else if (this.value === "contrôle") {
        controleFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        representationFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var typeAC = document.getElementById("typeACC");
        var structure_id = document.getElementById("structure_idC");
        var methodologie = document.getElementById("methodologieC");
        typeAC.required = true;
        structure_id.required = true;
        methodologie.required = true;
    } else if (this.value === "reunion") {
        reunionFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        representationFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        var modeSuivi = document.getElementById("modeSuivi");
        var ordreJour = document.getElementById("ordreJour");
        var responsable = document.getElementById("responsable");
        var nomAnimateur = document.getElementById("nomAnimateurR");
        var nomRapporteur = document.getElementById("nomRapporteurR");
        var decisionPrise = document.getElementById("decisionPrise");

        modeSuivi.required = true;
        ordreJour.required = true;
        responsable.required = true;
        nomAnimateur.required = true;
        nomRapporteur.required = true;
        decisionPrise.required = true;
    } else if (this.value === "représentation") {
        representationFields.style.display = "block";
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
        var nomEvenement = document.getElementById("nomEvenement");
        var representant = document.getElementById("representant");
        var positionRepresente = document.getElementById("positionRepresente");
        var description = document.getElementById("descriptionR");
        nomEvenement.required = true;
        representant.required = true;
        positionRepresente.required = true;
        description.required = true;
    } else {
        atelierFields.style.display = "none";
        auditFields.style.display = "none";
        seminaireFields.style.display = "none";
        interventionFields.style.display = "none";
        controleFields.style.display = "none";
        conferenceFields.style.display = "none";
        reunionFields.style.display = "none";
    }
});
